mapper.update:
update `iplog`
set ip = #{ip,jdbcType=VARCHAR},
  state = #{state,jdbcType=TINYINT},
  userName = #{userName,jdbcType=VARCHAR},
  loginTime = #{loginTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, ip, state, userName, loginTime
from `iplog`
where id = #{id,jdbcType=BIGINT}
